import React, { useState, useEffect } from 'react';
import { Calendar, MapPin, DollarSign, Plus, Filter, Search, Eye, Edit, Trash2, BarChart3, Clock, CheckCircle, AlertCircle, XCircle, Pause, List, Download, History, TreePine, Archive, Copy, Users, ChevronRight, ChevronDown, X, FolderOpen, Folder, File } from 'lucide-react';

// Asset types and their unit configurations
const assetTypes = {
  'FRT': { name: 'Forest', unit: 'Hectares', prefix: 'FRT', assetClass: 'Trees' },
  'WTD': { name: 'Wetland', unit: 'SM', prefix: 'WTD', assetClass: 'Wetlands' },
  'UBT': { name: 'Urban Trees', unit: 'Each', prefix: 'UBT', assetClass: 'Trees' },
  'GRS': { name: 'Grass', unit: 'SM', prefix: 'GRS', assetClass: 'Grass' }
};

// New hierarchical cost database
const costDatabase = {
  "Trees": {
    "Urban Street Trees": {
      "Pruning": { cost: 80, unit: "Each", description: "Regular pruning maintenance" },
      "Watering": { cost: 15, unit: "Each", description: "Irrigation services" },
      "Mulching": { cost: 25, unit: "Each", description: "Mulch application" },
      "Health Assessment": { cost: 75, unit: "Each", description: "Tree health evaluation" },
      "Replacement": { cost: 500, unit: "Each", description: "Tree replacement" }
    },
    "Forest Trees": {
      "Health Assessment": { cost: 400, unit: "Hectares", description: "Comprehensive forest health evaluation" },
      "Reforestation": { cost: 3000, unit: "Hectares", description: "Tree planting and establishment" },
      "Maintenance": { cost: 800, unit: "Hectares", description: "General forest maintenance" },
      "Inspection": { cost: 200, unit: "Hectares", description: "Regular forest inspection" }
    }
  },
  "Wetlands": {
    "Stormwater Management": {
      "Inspection": { cost: 0.5, unit: "SM", description: "Regular maintenance inspection" },
      "Sediment Removal": { cost: 8, unit: "SM", description: "Sediment cleaning and removal" },
      "Vegetation Management": { cost: 3, unit: "SM", description: "Aquatic vegetation control" },
      "Water Quality Testing": { cost: 1.5, unit: "SM", description: "Water quality assessment" }
    }
  },
  "Grass": {
    "Turf Management": {
      "Mowing": { cost: 2, unit: "SM", description: "Regular grass cutting" },
      "Fertilizing": { cost: 4, unit: "SM", description: "Fertilizer application" },
      "Aeration": { cost: 3, unit: "SM", description: "Soil aeration service" },
      "Overseeding": { cost: 15, unit: "SM", description: "Grass seed application" }
    }
  }
};

// Sample natural assets
const initialAssets = [
  { id: 'FRT-000001', name: 'Central Park Forest', type: 'FRT', region: 'Downtown', size: 12.5, installDate: '1995-05-15', expectedLifespan: 100, conditionScore: 8, lastInspection: '2024-03-15' },
  { id: 'FRT-000002', name: 'Riverside Forest Reserve', type: 'FRT', region: 'North Side', size: 25.8, installDate: '1987-09-20', expectedLifespan: 100, conditionScore: 7, lastInspection: '2024-02-28' },
  { id: 'FRT-000003', name: 'West End Forest Park', type: 'FRT', region: 'West Side', size: 18.2, installDate: '1992-08-10', expectedLifespan: 100, conditionScore: 9, lastInspection: '2024-03-20' },
  { id: 'FRT-000004', name: 'South Hills Forest', type: 'FRT', region: 'South Side', size: 31.5, installDate: '1985-11-05', expectedLifespan: 100, conditionScore: 6, lastInspection: '2024-02-12' },
  { id: 'WTD-000001', name: 'Miller Creek Wetland', type: 'WTD', region: 'East Side', size: 8500, installDate: '2010-04-12', expectedLifespan: 50, conditionScore: 9, lastInspection: '2024-01-20' },
  { id: 'WTD-000002', name: 'Industrial Park Wetland', type: 'WTD', region: 'West Side', size: 15200, installDate: '2018-07-08', expectedLifespan: 50, conditionScore: 8, lastInspection: '2024-03-10' },
  { id: 'WTD-000003', name: 'North Valley Wetland', type: 'WTD', region: 'North Side', size: 12300, installDate: '2015-06-15', expectedLifespan: 50, conditionScore: 7, lastInspection: '2024-02-25' },
  { id: 'UBT-000001', name: 'Main Street Tree Line', type: 'UBT', region: 'Downtown', size: 45, installDate: '2015-03-22', expectedLifespan: 80, conditionScore: 7, lastInspection: '2024-02-15' },
  { id: 'UBT-000002', name: 'Park Avenue Trees', type: 'UBT', region: 'North Side', size: 38, installDate: '2012-05-18', expectedLifespan: 80, conditionScore: 6, lastInspection: '2024-01-30' },
  { id: 'UBT-000003', name: 'Elm Street Trees', type: 'UBT', region: 'East Side', size: 52, installDate: '2017-04-10', expectedLifespan: 80, conditionScore: 8, lastInspection: '2024-03-08' },
  { id: 'UBT-000004', name: 'Oak Boulevard Trees', type: 'UBT', region: 'West Side', size: 67, installDate: '2014-09-12', expectedLifespan: 80, conditionScore: 7, lastInspection: '2024-02-18' },
  { id: 'GRS-000001', name: 'Municipal Sports Complex', type: 'GRS', region: 'South Side', size: 12000, installDate: '2019-06-10', expectedLifespan: 15, conditionScore: 8, lastInspection: '2024-03-05' },
  { id: 'GRS-000002', name: 'Riverside Park Lawn', type: 'GRS', region: 'East Side', size: 8500, installDate: '2020-04-25', expectedLifespan: 15, conditionScore: 9, lastInspection: '2024-02-20' },
  { id: 'GRS-000003', name: 'Community Center Grounds', type: 'GRS', region: 'Downtown', size: 6800, installDate: '2021-03-18', expectedLifespan: 15, conditionScore: 9, lastInspection: '2024-03-12' }
];

// Sample projects with auto-generated IDs
const initialProjects = [
  {
    id: 1,
    projectId: 'PROJ-2024-001',
    name: "Urban Forest Management Program",
    description: "Comprehensive management and maintenance of urban forest assets",
    status: "In Progress",
    startDate: "2024-01-15",
    endDate: "2025-06-30",
    completion: 0,
    team: "Environmental Services",
    region: "Citywide",
    createdBy: "admin",
    createdDate: "2024-01-10",
    lastModified: "2024-03-15",
    actions: [
      { 
        id: 1, 
        name: "Tree Pruning - UBT-000001 - Mar 2025", 
        assetId: 'UBT-000001', 
        actionPath: "Trees > Urban Street Trees > Pruning",
        status: "Completed", 
        nextDue: "2025-03-15", 
        overrideDate: null,
        recurrence: true, 
        recurrenceValue: 2, 
        recurrenceUnit: "years", 
        assetPercentage: 75,
        modeledCost: 2700,
        overrideCost: null,
        cost: 2700,
        completedDate: "2024-11-15",
        createdBy: "john.doe",
        createdDate: "2024-01-15"
      },
      { 
        id: 2, 
        name: "Forest Health Assessment - FRT-000001", 
        assetId: 'FRT-000001', 
        actionPath: "Trees > Forest Trees > Health Assessment",
        status: "In Progress", 
        nextDue: "2025-02-15", 
        overrideDate: "2025-03-01",
        recurrence: false, 
        assetPercentage: 100,
        modeledCost: 5000,
        overrideCost: 6500,
        cost: 6500,
        createdBy: "jane.smith",
        createdDate: "2024-02-01"
      },
      { 
        id: 3, 
        name: "Wetland Maintenance - WTD-000001 - Apr 2025", 
        assetId: 'WTD-000001', 
        actionPath: "Wetlands > Stormwater Management > Inspection",
        status: "Open", 
        nextDue: "2025-04-10", 
        overrideDate: null,
        recurrence: true, 
        recurrenceValue: 1, 
        recurrenceUnit: "years", 
        assetPercentage: 60,
        modeledCost: 2550,
        overrideCost: null,
        cost: 2550,
        createdBy: "mike.wilson",
        createdDate: "2024-01-20"
      }
    ]
  },
  {
    id: 2,
    projectId: 'PROJ-2024-002',
    name: "Green Infrastructure Expansion",
    description: "Expansion of wetland and grassland areas for stormwater management",
    status: "Planning",
    startDate: "2025-03-01",
    endDate: "2025-12-31",
    completion: 0,
    team: "Water Management",
    region: "West Side",
    createdBy: "admin",
    createdDate: "2024-02-15",
    lastModified: "2024-03-10",
    actions: [
      { 
        id: 4, 
        name: "Grass Overseeding - GRS-000002", 
        assetId: 'GRS-000002', 
        actionPath: "Grass > Turf Management > Overseeding",
        status: "Open", 
        nextDue: "2025-05-15", 
        overrideDate: null,
        recurrence: false, 
        assetPercentage: 25,
        modeledCost: 31875,
        overrideCost: 35000,
        cost: 35000,
        createdBy: "sarah.brown",
        createdDate: "2024-02-20"
      },
      { 
        id: 5, 
        name: "Wetland Inspection - WTD-000002", 
        assetId: 'WTD-000002', 
        actionPath: "Wetlands > Stormwater Management > Inspection",
        status: "Open", 
        nextDue: "2025-06-01", 
        overrideDate: null,
        recurrence: false, 
        assetPercentage: 100,
        modeledCost: 7600,
        overrideCost: null,
        cost: 7600,
        createdBy: "david.jones",
        createdDate: "2024-03-01"
      }
    ]
  }
];

// Archive storage
const initialArchives = {
  projects: [],
  actions: []
};

// Audit trail
const initialAuditTrail = [
  { id: 1, entityType: 'Project', entityId: 1, field: 'status', oldValue: 'Planning', newValue: 'In Progress', changedBy: 'admin', changedDate: '2024-01-15T10:30:00' },
  { id: 2, entityType: 'Action', entityId: 1, field: 'status', oldValue: 'Open', newValue: 'Completed', changedBy: 'john.doe', changedDate: '2024-11-15T14:20:00' }
];

const teams = ["Environmental Services", "Water Management", "Parks & Recreation", "Urban Planning"];
const regions = ["Downtown", "North Side", "South Side", "East Side", "West Side", "Citywide"];

const ProjectsActionsModule = () => {
  const [currentView, setCurrentView] = useState('projects');
  const [projectsTab, setProjectsTab] = useState('all');
  const [projectDetailTab, setProjectDetailTab] = useState('project');
  const [projects, setProjects] = useState(initialProjects);
  const [assets, setAssets] = useState(initialAssets);
  const [archives, setArchives] = useState(initialArchives);
  const [auditTrail, setAuditTrail] = useState(initialAuditTrail);
  const [selectedProject, setSelectedProject] = useState(null);
  const [showActionModal, setShowActionModal] = useState(false);
  const [showBulkActionModal, setShowBulkActionModal] = useState(false);
  const [showProjectModal, setShowProjectModal] = useState(false);
  const [showActionSelector, setShowActionSelector] = useState(false);
  const [editingAction, setEditingAction] = useState(null);
  const [editingProject, setEditingProject] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [assetSearchTerm, setAssetSearchTerm] = useState('');
  const [filters, setFilters] = useState({
    projectType: '',
    status: '',
    region: '',
    assetClass: '',
    team: ''
  });

  // Tree view state
  const [expandedNodes, setExpandedNodes] = useState(new Set(['Trees', 'Wetlands', 'Grass']));

  // Action selector state
  const [selectorState, setSelectorState] = useState({
    selectedAssetClass: '',
    selectedAssetType: '',
    selectedAction: '',
    breadcrumbs: [],
    expandedNodes: new Set()
  });

  // Action modal state
  const [actionForm, setActionForm] = useState({
    assetId: '',
    actionPath: '',
    actionDetails: null,
    recurrence: false,
    recurrenceValue: 1,
    recurrenceUnit: 'years',
    nextDue: '',
    overrideDate: '',
    assetPercentage: 100,
    modeledCost: 0,
    overrideCost: ''
  });

  // Bulk action modal state
  const [bulkActionForm, setBulkActionForm] = useState({
    region: '',
    assetType: '',
    actionPath: '',
    actionDetails: null,
    recurrence: false,
    recurrenceValue: 1,
    recurrenceUnit: 'years',
    nextDue: '',
    overrideDate: '',
    assetPercentage: 100,
    overrideCost: ''
  });

  // Project modal state
  const [projectForm, setProjectForm] = useState({
    name: '',
    description: '',
    status: 'Planning',
    startDate: '',
    endDate: '',
    team: '',
    region: ''
  });

  // Generate project ID
  const generateProjectId = () => {
    const year = new Date().getFullYear();
    const existingCount = projects.filter(p => p.projectId.includes(`PROJ-${year}`)).length;
    return `PROJ-${year}-${String(existingCount + 1).padStart(3, '0')}`;
  };

  // Generate recurring actions for 10-year plan
  const generateRecurringActions = (baseAction, project) => {
    if (!baseAction.recurrence) return [baseAction];

    const actions = [baseAction];
    const startDate = new Date(baseAction.nextDue);
    const currentYear = new Date().getFullYear();

    for (let year = startDate.getFullYear() + baseAction.recurrenceValue; year <= currentYear + 10; year += baseAction.recurrenceValue) {
      const recurringDate = new Date(startDate);
      recurringDate.setFullYear(year);

      const recurringAction = {
        ...baseAction,
        id: Date.now() + Math.random(),
        name: generateActionName(baseAction.actionPath.split(' > ').pop(), baseAction.assetId, recurringDate.toISOString().split('T')[0], null, true),
        nextDue: recurringDate.toISOString().split('T')[0],
        status: 'Open',
        completedDate: undefined,
        createdDate: new Date().toISOString()
      };

      actions.push(recurringAction);
    }

    return actions;
  };

  // Calculate project completion
  const calculateProjectCompletion = (project) => {
    const totalCost = project.actions.reduce((sum, action) => sum + action.cost, 0);
    const completedCost = project.actions
      .filter(action => action.status === 'Completed')
      .reduce((sum, action) => sum + action.cost, 0);

    return totalCost > 0 ? Math.round((completedCost / totalCost) * 100) : 0;
  };

  // Update project completions
  useEffect(() => {
    setProjects(prev => prev.map(project => ({
      ...project,
      completion: calculateProjectCompletion(project)
    })));
  }, []);

  const addAuditEntry = (entityType, entityId, field, oldValue, newValue, changedBy = 'current_user') => {
    const newEntry = {
      id: Date.now(),
      entityType,
      entityId,
      field,
      oldValue,
      newValue,
      changedBy,
      changedDate: new Date().toISOString()
    };
    setAuditTrail(prev => [newEntry, ...prev]);
  };

  const calculateModeledCost = (assetId, actionDetails, percentage) => {
    const asset = assets.find(a => a.id === assetId);
    if (!asset || !actionDetails || !percentage) return 0;

    return Math.round(asset.size * (percentage / 100) * actionDetails.cost);
  };

  const getStatusIcon = (status) => {
    switch (status) {
      case 'Completed': return <CheckCircle className="w-4 h-4 text-green-600" />;
      case 'In Progress': return <Clock className="w-4 h-4 text-blue-600" />;
      case 'Open': return <AlertCircle className="w-4 h-4 text-yellow-600" />;
      case 'Cancelled': return <XCircle className="w-4 h-4 text-red-600" />;
      case 'Planning': return <Pause className="w-4 h-4 text-blue-500" />;
      case 'Archived': return <Archive className="w-4 h-4 text-gray-600" />;
      default: return <AlertCircle className="w-4 h-4 text-gray-600" />;
    }
  };

  const getStatusBadge = (status) => {
    const baseClasses = "px-2 py-1 text-xs font-medium rounded";
    switch (status) {
      case 'Completed': return `${baseClasses} bg-green-100 text-green-800`;
      case 'In Progress': return `${baseClasses} bg-blue-100 text-blue-800`;
      case 'Open': return `${baseClasses} bg-yellow-100 text-yellow-800`;
      case 'Cancelled': return `${baseClasses} bg-red-100 text-red-800`;
      case 'Planning': return `${baseClasses} bg-orange-100 text-orange-800`;
      case 'Archived': return `${baseClasses} bg-gray-100 text-gray-800`;
      default: return `${baseClasses} bg-gray-100 text-gray-800`;
    }
  };

  const generateActionName = (actionType, assetId, nextDue, overrideDate, recurrence) => {
    if (!actionType || !assetId) return '';

    let name = `${actionType} - ${assetId}`;

    if (recurrence && (overrideDate || nextDue)) {
      const date = new Date(overrideDate || nextDue);
      const monthNames = ["Jan", "Feb", "Mar", "Apr", "May", "Jun", "Jul", "Aug", "Sep", "Oct", "Nov", "Dec"];
      const month = monthNames[date.getMonth()];
      const year = date.getFullYear();
      name += ` - ${month} ${year}`;
    }

    return name;
  };

  const filteredProjects = projects.filter(project => {
    if (projectsTab === 'archived' && project.status !== 'Archived') return false;
    if (projectsTab === 'all' && project.status === 'Archived') return false;

    const matchesSearch = !searchTerm || 
      project.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
      project.description.toLowerCase().includes(searchTerm.toLowerCase()) ||
      project.region.toLowerCase().includes(searchTerm.toLowerCase());

    const matchesFilters = (!filters.status || project.status === filters.status) &&
                          (!filters.team || project.team === filters.team) &&
                          (!filters.region || project.region === filters.region);

    return matchesSearch && matchesFilters;
  });

  const filteredAssets = (searchTerm = assetSearchTerm) => {
    if (!searchTerm) return assets;
    return assets.filter(asset =>
      asset.id.toLowerCase().includes(searchTerm.toLowerCase()) ||
      asset.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
      assetTypes[asset.type].name.toLowerCase().includes(searchTerm.toLowerCase()) ||
      asset.region.toLowerCase().includes(searchTerm.toLowerCase())
    );
  };

  const downloadActionsCSV = () => {
    const csvData = [];
    projects.forEach(project => {
      project.actions.forEach(action => {
        const asset = assets.find(a => a.id === action.assetId);
        const assetConfig = assetTypes[asset?.type];
        const actionYear = new Date(action.overrideDate || action.nextDue || action.completedDate || '').getFullYear();

        csvData.push({
          'Project Name': project.name,
          'Project ID': project.projectId,
          'Region Name': project.region,
          'Asset Class': assetConfig?.assetClass || '',
          'Asset Name': asset?.name || '',
          'Asset Size': asset?.size || '',
          'Action Name': action.name,
          'Action Cost': action.cost,
          'Action Year': actionYear || '',
          'Status': action.status,
          'Due Date': action.overrideDate || action.nextDue || '',
          'Completed Date': action.completedDate || ''
        });
      });
    });

    const csv = [
      Object.keys(csvData[0]).join(','),
      ...csvData.map(row => Object.values(row).map(value => `"${value}"`).join(','))
    ].join('\n');

    const blob = new Blob([csv], { type: 'text/csv' });
    const url = window.URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = 'actions-list-export.csv';
    a.click();
